<?php	include_once './classes/DataBaseManager_class.php';	include_once './classes/Produit_class.php';	include_once './include/commonFunctions.php';	class ProduitManager	{		public static function afficherListeProduits()		{			$co = DataBaseManager::getInstance();			$req = $co->execRequete("SELECT m.* FROM produit m ORDER BY m.nom");			$err = "";			while ($donnees = mysqli_fetch_assoc($req))			{				$produit = Produit::getProduitFromForm2($err, $donnees["nom"],				$donnees["prix"],$donnees["categorie"], $donnees["provenance"]);				if (empty($err)) $produit->afficher();			}			$co->libereResultatRequete($req);		}	} ?>